import React from "react";
import "./style.css";
import Square from "./Square";

// function Board() {
//     return (
//         <div className="board">
//             <h1>井字棋游戏--React</h1>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//             <button className="btn"></button>
//         </div>
//     );
// }
//

class Board extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(""),
            player:"x",
            winner:"",
            history:[
                {
                    squares: Array(9).fill(""),
                    player: "X"
                },
            ],
            step:1,
            winnerArr:[],
        };
    }

    render() {
        let { player, squares, winner } = this.state;
        let title = "";
        if (!winner) {
            title = <p>开局是：{player}</p>;
        } else {
            title = <p>大吉大利：{winner}</p>;
        }
        return (
            <div className="board">
                <h1>井字棋游戏--React</h1>
                {title}
                {squares.map((el, index) => {
                    return (
                        <Square key={index} player={el} changePlayer={() => {this.changePlayer(index);}} winnerClass={this.getClassName(index)}></Square>
                    );
                })}
            </div>
        );
    }

    calculateWinner(squares){
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];
        for(let i = 0; i < lines.length; i++){
            const[a, b, c] = lines[i];
            if (
                squares[a] && squares[a] === squares [b] && squares[a] === squares[c]
            )
            {
                return{
                    squares:squares[a],
                    winnerArr: lines[i],
                };
            }
        }
        return null;
    }

    changePlayer(i){
        if (this.state.winner){
            return;
        }
        let squares = [...this.state.squares];
        let history = this.state.history.slice(0,this.state.step);
        if (squares[i]){
            return;
        }
        let player = this.state.player === "X" ? "O" : "X";
        squares[i] = this.state.player;
        history.push(
            {
                squares,
                player,
            }
        );
        this.setState({
            player,
            squares,
            history,
            step:history.length,
        });
        let winner = this.calculateWinner(squares);
        if (winner){
            this.setState({
                winner:winner.squares,
                winnerArr:winner.winnerArr,
            });
        }
    }

    getClassName(index){
        let {winner, winnerArr } = this.state;
        if (winner) {
            for (let i = 0; i < 3; i++){
                if (winnerArr[i] === index){
                    return"winner-square";
                }
            }
            return"";
        }
        else {
            return "";
        }
    }
}

export default Board;
