class Board extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(""),
            player:"x",
            winner:"",
            winnerArr:[],
        };
    }

    // render() {
    //     let {player, squares, winner} = this.state;
    //     let title = "";
    //     if (!winner){
    //         title = <p>下一位：{player}</p>;
    //     }
    //     else {
    //         title = <p>大吉大利！：{winner}</p>;
    //     }
    //     return (
    //         <div className="board">
    //             <h1>井字棋游戏- -React</h1>
    //             {title}
    //             {squares.map((el,index) => {
    //                 return(
    //                     <Square key={index} player={el} changePlayer={() => {this.changePlayer(index)}}></Square>;
    //                 )
    //             })}
    //             {/*{this.state.squares.map((el,index) =>{*/}
    //             {/*    return <Square key={index} player={el} changePlayer={() => {this.changePlayer(index)}}></Square>;*/}
    //             {/*})}*/}
    //         </div>
    //     );
    // }

    render() {
        let { player, squares, winner } = this.state;
        let title = "";
        if (!winner) {
            title = <p>开局是：{player}</p>;
        } else {
            title = <p>大吉大利：{winner}</p>;
        }
        return (
            <div className="board">
                <h1>井字棋游戏--React</h1>
                {title}
                {squares.map((el, index) => {
                    return (
                        <Square key={index} player={el} changePlayer={() => {this.changePlayer(index);}} ></Square>
                    );
                })}
            </div>
        );
    }

    calculateWinner(squares){
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];
        for(let i = 0; i < lines.length; i++){
            const[a, b, c] = lines[i];
            if (
                squares[a] && squares[a] === squares [b] && squares[a] === squares[c]
            )
            {
                return{
                    squares:squares[a],
                    winnerArr: lines[i],
                };
            }
        }
        return null;
    }

    changePlayer(index){
        let player = this.state.player === "X" ? "O" : "X";
        let squares = [...this.state.squares];
        squares[index] = player;
        this.setState({
            player,
            squares
        });
        let winner = this.calculateWinner(squares);
        if (winner){
            this.setState({
                winner:winner.squares,
                winnerArr:winner.winnerArr,
            });
        }
    }

    getClassName(index){
        let {winner, winnerArr } = this.state;
        if (winner) {
            for (let i = 0; i < 3; i++){
                if (winnerArr[i] === index){
                    return"winner-square";
                }
            }
            return"";
        }
        else {
            return "";
        }
    }
}


export default Board;
